async function getData(){try{var e=await fetch("json/data.min.json");if(!e.ok)throw new Error("Помилка при завантаженні даних");renderData(await e.json())}catch(e){console.error("Помилка під час отримання даних:",e)}}function renderData(o){document.querySelector(".michel").textContent=o.name.first,document.querySelector(".rigaurio").textContent=o.name.last,document.querySelector(".ui").textContent=o.profession,document.querySelector(".photo").src=o.photo,o.education.forEach((e,t)=>{document.querySelector(`.education-content .col-6:nth-child(${t+1}) .sub-title`).textContent=e.major,document.querySelector(`.education-content .col-6:nth-child(${t+1}) .sub-sub-title`).innerHTML=e.university+"<br>"+e.years}),o.experience.forEach((e,t)=>{var n=0===t?"":"-1";document.querySelector(".experience_content .sub-title"+n).textContent=e.position,document.querySelector(".experience_content .sub-1"+n).textContent=e.duration,document.querySelector(`.experience_content .sub-2:nth-child(${t+1})`).textContent=e.company,document.querySelector(`.experience_content .experience:nth-child(${t+1}) .sub-sub-title`).innerHTML=e.description}),o.expertise.forEach((e,t)=>{document.querySelector(`.expertise-content .col-3:nth-child(${t+1}) .donut-hole .text`).innerHTML=e.replace(" ","<br>")}),document.querySelector(".about-me-content .sub-sub-title-1").innerHTML=o.aboutMe.replace(/\n/g,"<br>"),document.querySelectorAll(".contact-info-content .contact").forEach((e,t)=>{var n=e.textContent.toLowerCase();e.nextElementSibling.querySelector("a").textContent=o.contactInfo[n]});let t=document.querySelector(".hobbies-content .d-flex");t.innerHTML="",o.hobbies.forEach(e=>{t.innerHTML+=`
      <div class="${e.name.toLowerCase()}">
        <img src="${e.icon}" class="t">
        <p class="phrase-1">${e.name}</p>
      </div>
    `})}$(document).ready(function(){$(".button").click(function(e){$(this).toggleClass("rotated active").closest(".section").find(".education-content").slideToggle(300)}),$(".button").click(function(e){$(this).toggleClass("rotated active").closest(".section-2").find(".experience_content").slideToggle(300)}),$(".button").click(function(e){$(this).toggleClass("rotated active").closest(".section-2").find(".expertise-content").slideToggle(300)}),$(".button-1").click(function(e){$(this).toggleClass("rotated active").closest(".section-1").find(".about-me-content").slideToggle(300)}),$(".button-1").click(function(e){$(this).toggleClass("rotated active").closest(".section-1").find(".contact-info-content").slideToggle(300)}),$(".button-1").click(function(e){$(this).toggleClass("rotated active").closest(".section-1").find(".hobbies-content").slideToggle(300)})}),getData();