async function getData(){try{var t=await fetch("http://127.0.0.1:8080/data/data.json");if(console.log("Response:",t),!t.ok)throw new Error("Помилка при завантаженні даних");var e=await t.json();console.log("Data:",e),renderData(e)}catch(t){console.error("Помилка під час отримання даних:",t)}}function renderData(t){document.getElementById("data").innerHTML=`
    <h1>${t.first} ${t.last}</h1>
    <h2>${t.profession}</h2>
    <img src="${t.photo}" alt="${t.first}'s photo" class="img-fluid mb-3">
    <h3>About Me</h3>
    <p>${t.aboutMe}</p>
    <h3>Contact Info</h3>
    <p>Phone: <a href="tel:${t.contactInfo.phone}">${t.contactInfo.phone}</a></p>
    <p>Email: <a href="mailto:${t.contactInfo.email}">${t.contactInfo.email}</a></p>
    <p>Address: ${t.contactInfo.address}</p>
    <h3>Education</h3>
    ${t.education.map(t=>`<p><strong>${t.major}</strong><br>${t.university} (${t.years})</p>`).join("")}
    <h3>Experience</h3>
    ${t.experience.map(t=>`<p><strong>${t.position}</strong> at ${t.company}<br>${t.duration}<br>${t.description}</p>`).join("")}
    <h3>Expertise</h3>
    <ul>${t.expertise.map(t=>`<li>${t}</li>`).join("")}</ul>
    <h3>Hobbies</h3>
    <div class="d-flex flex-wrap">
      ${t.hobbies.map(t=>`<div class="me-3 text-center"><img src="${t.icon}" alt="${t.name}" style="width: 50px;"><br>${t.name}</div>`).join("")}
    </div>
  `}$(document).ready(function(){$(".button").click(function(t){$(this).toggleClass("rotated active").closest(".section").find(".education-content").slideToggle(300)}),$(".button").click(function(t){$(this).toggleClass("rotated active").closest(".section-2").find(".experience_content").slideToggle(300)}),$(".button").click(function(t){$(this).toggleClass("rotated active").closest(".section-2").find(".expertise-content").slideToggle(300)}),$(".button-1").click(function(t){$(this).toggleClass("rotated active").closest(".section-1").find(".about-me-content").slideToggle(300)}),$(".button-1").click(function(t){$(this).toggleClass("rotated active").closest(".section-1").find(".contact-info-content").slideToggle(300)}),$(".button-1").click(function(t){$(this).toggleClass("rotated active").closest(".section-1").find(".hobbies-content").slideToggle(300)})}),document.getElementById("loadDataBtn").addEventListener("click",getData),document.getElementById("loseDataBtn").addEventListener("click",()=>{document.getElementById("data").innerHTML=""});